mapper.select:
select * from ht_mode


    and modeId = #{modeid}


    and modeName like concat('%',#{modename},'%')

select count(1) from ht_mode


    and modeName like concat('%',#{modeName},'%')


    and modeId = #{modeId}

select * from ht_mode


    and modeName like concat('%',#{modeName},'%')


    and modeId = #{modeId}


limit #{startLimitPos},#{pageSize}
